// This function reformats the timeseries data into an array that 
// Chart.js (See http://www.chartjs.org/ ) will plot
// The node-red-dashboard Chart node uses Chart.js 

// The Array has this structure, inserting the first element as a template.
var TempChartData = [ {"series":["Data"],
                   "data":[[{"x":msg.payload[0].time,"y":msg.payload[0].temp}]],
                   "labels":["Data"]}];

var HumChartData = [ {"series":["Data"],
                   "data":[[{"x":msg.payload[0].time,"y":msg.payload[0].humidity}]],
                   "labels":["Data"]}];

// Start at 1 because we've already added element 0 in the initial array definition
for( var i=1; i < msg.payload.length; i++ ) {
    TempChartData[0].data[0].push( { "x":msg.payload[i].time,"y":msg.payload[i].temp } );
    HumChartData[0].data[0].push(  { "x":msg.payload[i].time,"y":msg.payload[i].humidity } );
}
// Now we need to sort on the time so that we give the Chart node an array in time order
TempChartData[0].data[0].sort(function sortNumber(a,b) { return a.x - b.x; });
HumChartData[0].data[0].sort(function sortNumber(a,b) { return a.x - b.x; });

msg.payload = TempChartData;
var msg1 = {};
msg1.payload = HumChartData;
